    "dashboard:read",
    "movements:read",
    "movements:write",
    "inventory:read",
    "reports:read",
    "masters:read",
  ],
};

export const isRole = (value: string | null | undefined): value is Role =>
  !!value && (ROLES as readonly string[]).includes(value);

