export const listUsuarios = async ({
  page = 1,
  pageSize = 20,
}: {
  page?: number;
  pageSize?: number;
}) => {
  const offset = (page - 1) * pageSize;
  const response = await auth.api.listUsers({
    headers: headers(),
    query: {
      limit: pageSize,
      offset,
      sortBy: "name",
      sortDirection: "asc",
    },
  });

  return {
    items: response.users.map((user) => ({
      id: user.id,
      email: user.email,
      nombre: user.name,
      estado: user.banned ? "inactivo" : "activo",
      rolId: user.role ?? "bodeguero",
      rolNombre: user.role ?? "bodeguero",
    })),
    total: response.total,
  };
};

export const getUsuarioById = async (id: string) => {
  const user = await auth.api.getUser({
    headers: headers(),
    query: {
      id,
    },
  });

  return {
    id: user.id,
    email: user.email,
    nombre: user.name,
    estado: user.banned ? "inactivo" : "activo",
    rolId: user.role ?? "bodeguero",
    rolNombre: user.role ?? "bodeguero",
  };
};

export const listRoles = async () => {
  return ROLES.map((role) => ({
    id: role,
    nombre: role,
  }));
};

