+import { cookies, headers } from "next/headers";
+import { redirect } from "next/navigation";
+
+import { LoginForm } from "@/components/auth/login-form";
+import { PasswordResetForm } from "@/components/auth/password-reset-form";
+import {
+  Card,
+  CardContent,
+  CardDescription,
+  CardHeader,
+  CardTitle,
+} from "@/components/ui/card";
+import { auth } from "@/lib/auth/auth";
+import {
+  DEFAULT_AUTHENTICATED_REDIRECT,
+  NEXT_COOKIE_NAME,
+  NEXT_QUERY_PARAM,
+  resolveNextPath,
+} from "@/lib/auth/redirects";
+
+type LoginPageProps = {
+  searchParams?: Record<string, string | string[] | undefined>;
+};
+
+const getParam = (value?: string | string[]) =>
+  Array.isArray(value) ? value[0] : value;
+
+export default async function LoginPage({ searchParams }: LoginPageProps) {
+  const session = await auth.api.getSession({ headers: headers() });
+  const nextFromQuery = resolveNextPath(
+    getParam(searchParams?.[NEXT_QUERY_PARAM]),
+  );
+  const nextFromCookie = resolveNextPath(
+    cookies().get(NEXT_COOKIE_NAME)?.value,
+  );
+  const nextPath =
+    nextFromQuery ?? nextFromCookie ?? DEFAULT_AUTHENTICATED_REDIRECT;
+
+  if (session?.user) {
+    redirect(nextPath);
+  }
+
+  const token = getParam(searchParams?.token);
+  const error = getParam(searchParams?.error);
+
+  return (
+    <div className="flex min-h-svh items-center justify-center bg-muted/30 px-4 py-10">
