  )

  React.useEffect(() => {
    setOpenSections((prev) => {
      const next = { ...prev }

      sections.forEach((section) => {
        const hasActiveItem = section.items.some((item) =>
          isActivePath(pathname, item.href)
        )

        if (next[section.id] === undefined) {
          next[section.id] = true
        }

        if (hasActiveItem) {
          next[section.id] = true
