  const { id, ...rest } = data;

  return bodegasRepository.actualizar(id, rest);
};

export const desactivarBodega = async (input: unknown) => {
  await resolveSessionUser("masters:write");
  const { id } = idPayloadSchema.parse(input);

  return bodegasRepository.desactivar(id);
};

