import type { AuditLogService } from "./audit-log.service";
import {
  aplicarSalidaInventario,
  calcularCostoPromedioPonderado,
} from "./inventory-valuation.service";

type MovimientoDetalle = {
  itemId: string;
  cantidad: number;
  costoUnitario?: number | null;
};

type Movimiento = {
  id: string;
  tipo: "entrada" | "salida" | "transferencia" | "ajuste";
  estado: "borrador" | "publicado" | "anulado";
  fecha: Date;
  bodegaOrigenId?: string | null;
  bodegaDestinoId?: string | null;
  usuarioId: string;
  detalles: MovimientoDetalle[];
};

type ItemBodega = {
  id: string;
  itemId: string;
  bodegaId: string;
  stockActual: number;
  costoPromedio: number;
};

export type MovimientosRepository = {
  obtenerPorId: (id: string) => Promise<Movimiento | null>;
  actualizarEstado: (id: string, estado: Movimiento["estado"]) => Promise<Movimiento>;
};

export type ItemBodegaRepository = {
  obtenerPorItemBodega: (
    itemId: string,
    bodegaId: string,
  ) => Promise<ItemBodega | null>;
  guardar: (input: ItemBodega) => Promise<ItemBodega>;
};

export type CreateMovementsServiceDeps = {
  movimientosRepository: MovimientosRepository;
  itemBodegaRepository: ItemBodegaRepository;
  auditLogService: AuditLogService;
};

export const createMovementsService = ({
  movimientosRepository,
  itemBodegaRepository,
  auditLogService,
}: CreateMovementsServiceDeps) => {
  const publicarMovimiento = async ({
    movimientoId,
    usuarioId,
    permitirNegativo,
  }: {
    movimientoId: string;
    usuarioId: string;
    permitirNegativo?: boolean;
  }) => {
    const movimiento = await movimientosRepository.obtenerPorId(movimientoId);

    if (!movimiento) {
      throw new Error("NOT_FOUND");
    }

    if (movimiento.estado !== "borrador") {
      throw new Error("CONFLICT");
    }

    if (movimiento.tipo === "entrada") {
      if (!movimiento.bodegaDestinoId) {
        throw new Error("CONFLICT");
      }

      await Promise.all(
        movimiento.detalles.map(async (detalle) => {
          const costoUnitario = detalle.costoUnitario;
          if (costoUnitario == null) {
            throw new Error("INVALID_COST");
          }

          const existente =
            (await itemBodegaRepository.obtenerPorItemBodega(
              detalle.itemId,
              movimiento.bodegaDestinoId!,
            )) ?? {
              id: "",
              itemId: detalle.itemId,
              bodegaId: movimiento.bodegaDestinoId!,
              stockActual: 0,
              costoPromedio: 0,
            };

          const actualizado = calcularCostoPromedioPonderado(existente, {
            cantidad: detalle.cantidad,
            costoUnitario,
          });

          await itemBodegaRepository.guardar({
            ...existente,
            ...actualizado,
          });
        }),
      );
    }

    if (movimiento.tipo === "salida") {
      if (!movimiento.bodegaOrigenId) {
        throw new Error("CONFLICT");
      }

      await Promise.all(
        movimiento.detalles.map(async (detalle) => {
          const existente =
            await itemBodegaRepository.obtenerPorItemBodega(
              detalle.itemId,
              movimiento.bodegaOrigenId!,
            );

          if (!existente) {
            throw new Error("NOT_FOUND");
          }

          const actualizado = aplicarSalidaInventario(existente, {
            cantidad: detalle.cantidad,
            permitirNegativo,
          });

          await itemBodegaRepository.guardar({
            ...existente,
            stockActual: actualizado.stockActual,
            costoPromedio: actualizado.costoPromedio,
          });
        }),
      );
    }

    if (movimiento.tipo === "transferencia") {
      if (!movimiento.bodegaOrigenId || !movimiento.bodegaDestinoId) {
        throw new Error("CONFLICT");
      }

      await Promise.all(
        movimiento.detalles.map(async (detalle) => {
          const origen =
            await itemBodegaRepository.obtenerPorItemBodega(
              detalle.itemId,
              movimiento.bodegaOrigenId!,
            );

          if (!origen) {
            throw new Error("NOT_FOUND");
          }

          const actualizadoOrigen = aplicarSalidaInventario(origen, {
            cantidad: detalle.cantidad,
            permitirNegativo,
          });

          await itemBodegaRepository.guardar({
            ...origen,
            stockActual: actualizadoOrigen.stockActual,
            costoPromedio: actualizadoOrigen.costoPromedio,
          });

          const destino =
            (await itemBodegaRepository.obtenerPorItemBodega(
              detalle.itemId,
              movimiento.bodegaDestinoId!,
            )) ?? {
              id: "",
              itemId: detalle.itemId,
              bodegaId: movimiento.bodegaDestinoId!,
              stockActual: 0,
              costoPromedio: 0,
            };

          const actualizadoDestino = calcularCostoPromedioPonderado(destino, {
            cantidad: detalle.cantidad,
            costoUnitario: origen.costoPromedio,
          });

          await itemBodegaRepository.guardar({
            ...destino,
            ...actualizadoDestino,
          });
        }),
      );
    }

    if (movimiento.tipo === "ajuste") {
      throw new Error("CONFLICT");
    }

    const actualizado = await movimientosRepository.actualizarEstado(
      movimientoId,
      "publicado",
    );

    await auditLogService.registrarEvento({
      entidad: "movimiento",
      entidadId: movimientoId,
      accion: "publicar",
      usuarioId,
      fecha: new Date(),
      metadata: {
        tipo: movimiento.tipo,
      },
    });

    return actualizado;
  };

  const anularMovimiento = async ({
    movimientoId,
    usuarioId,
    motivo,
  }: {
    movimientoId: string;
    usuarioId: string;
    motivo?: string;
  }) => {
    const movimiento = await movimientosRepository.obtenerPorId(movimientoId);

    if (!movimiento) {
      throw new Error("NOT_FOUND");
    }

    if (movimiento.estado !== "publicado") {
      throw new Error("CONFLICT");
    }

    const actualizado = await movimientosRepository.actualizarEstado(
      movimientoId,
      "anulado",
    );

    await auditLogService.registrarEvento({
      entidad: "movimiento",
      entidadId: movimientoId,
      accion: "anular",
      usuarioId,
      fecha: new Date(),
      metadata: {
        motivo,
      },
    });

    return actualizado;
  };

  return {
    publicarMovimiento,
    anularMovimiento,
  };
};

