"use client"

import { useEffect, useMemo, useState } from "react"
import { useRouter } from "next/navigation"

import { Button } from "@/components/ui/button"

type ItemOption = {
  id: string
  codigo: string
  nombre: string
  estado: string
}

type WarehouseItemAssignmentProps = {
  bodegaId: string
  bodegaEstado: string
  assignedItemIds: string[]
  canAssign?: boolean
}

export function WarehouseItemAssignment({
  bodegaId,
  bodegaEstado,
  assignedItemIds,
  canAssign = false,
}: WarehouseItemAssignmentProps) {
  const router = useRouter()
  const [items, setItems] = useState<ItemOption[]>([])
  const [selectedIds, setSelectedIds] = useState<string[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    let active = true

    const loadItems = async () => {
      setIsLoading(true)
      setError(null)

      try {
        const response = await fetch("/api/items?status=activo&page=1&pageSize=100")
        if (!response.ok) {
          throw new Error("FETCH_FAILED")
        }
        const data = await response.json()
        const options = Array.isArray(data?.items) ? data.items : []
        if (active) {
          setItems(options)
        }
      } catch {
        if (active) {
          setError("No se pudieron cargar los items activos.")
        }
      } finally {
        if (active) {
          setIsLoading(false)
        }
      }
    }

    void loadItems()

    return () => {
      active = false
    }
  }, [])

  const disabled = !canAssign || bodegaEstado === "inactivo"

  const availableItems = useMemo(
    () => items.filter((item) => item.estado !== "inactivo"),
    [items],
  )

  const toggleSelection = (itemId: string) => {
    setSelectedIds((prev) =>
      prev.includes(itemId)
        ? prev.filter((id) => id !== itemId)
        : [...prev, itemId],
    )
  }

  const handleSubmit = async () => {
    if (disabled || selectedIds.length === 0) {
      return
    }

    setIsSubmitting(true)
    setError(null)

    try {
      const response = await fetch(`/api/bodegas/${bodegaId}/items`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ itemIds: selectedIds }),
      })

      if (!response.ok) {
        const payload = await response.json().catch(() => null)
        const message = payload?.error

        if (message === "ITEM_ALREADY_ASSIGNED") {
          setError("Uno o mas items ya estaban asignados.")
        } else if (message === "ITEM_INACTIVE") {
          setError("Solo se pueden asignar items activos.")
        } else if (message === "BODEGA_INACTIVE") {
          setError("La bodega esta inactiva.")
        } else {
          setError("No se pudo asignar los items.")
        }
        return
      }

      setSelectedIds([])
      router.refresh()
    } catch {
      setError("Ocurrio un error inesperado.")
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <section className="flex flex-col gap-4 rounded-2xl border border-neutral-200 bg-white p-5 shadow-sm">
      <div>
        <h3 className="text-lg font-semibold text-neutral-900">
          Asignar items
        </h3>
        <p className="text-sm text-neutral-600">
          Selecciona items activos para asociarlos a esta bodega.
        </p>
      </div>

      {!canAssign ? (
        <div className="rounded-lg border border-neutral-200 bg-neutral-50 px-3 py-2 text-sm text-neutral-500">
          No tienes permisos para asignar items.
        </div>
      ) : null}

      {bodegaEstado === "inactivo" ? (
        <div className="rounded-lg border border-amber-200 bg-amber-50 px-3 py-2 text-sm text-amber-700">
          La bodega esta inactiva y no permite nuevas asignaciones.
        </div>
      ) : null}

      {isLoading ? (
        <div className="text-sm text-neutral-500">Cargando items...</div>
      ) : (
        <div className="grid gap-3">
          {availableItems.length === 0 ? (
            <div className="text-sm text-neutral-500">
              No hay items activos disponibles.
            </div>
          ) : (
            <div className="grid max-h-64 gap-2 overflow-y-auto rounded-lg border border-neutral-200 p-3 text-sm text-neutral-700">
              {availableItems.map((item) => {
                const isAssigned = assignedItemIds.includes(item.id)
                return (
                  <label
                    key={item.id}
                    className={`flex items-center justify-between gap-3 rounded-md px-2 py-1 ${
                      isAssigned ? "opacity-60" : ""
                    }`}
                  >
                    <div className="flex items-center gap-2">
                      <input
                        type="checkbox"
                        checked={selectedIds.includes(item.id)}
                        onChange={() => toggleSelection(item.id)}
                        disabled={disabled || isAssigned}
                      />
                      <span className="font-medium">
                        {item.codigo}
                      </span>
                      <span className="text-neutral-500">
                        {item.nombre}
                      </span>
                    </div>
                    {isAssigned ? (
                      <span className="text-xs text-neutral-400">Asignado</span>
                    ) : null}
                  </label>
                )
              })}
            </div>
          )}
        </div>
      )}

      {error ? (
        <div className="rounded-lg border border-destructive/20 bg-destructive/10 px-3 py-2 text-sm text-destructive">
          {error}
        </div>
      ) : null}

      <div className="flex justify-end">
        <Button
          type="button"
          disabled={disabled || isSubmitting || selectedIds.length === 0}
          onClick={handleSubmit}
        >
          {isSubmitting ? "Asignando..." : "Asignar items"}
        </Button>
      </div>
    </section>
  )
}

